import React, { useEffect, useMemo, useState } from "react";
import {
  Form,
  Input,
  InputNumber,
  Popconfirm,
  Table,
  Typography,
  SelectProps,
  Select,
  Space,
  Image,
} from "antd";
import { OrderItem } from "@/types/order";
import useGetOrder from "@/hooks/useGetOrders";
import { useRecoilValue } from "recoil";
import masterDataState from "@/stores/masterData";

interface EditableCellProps extends React.HTMLAttributes<HTMLElement> {
  editing: boolean;
  dataIndex: string;
  title: any;
  inputType: "number" | "text" | "select";
  record: OrderItem;
  index: number;
  children: React.ReactNode;
  options?: SelectProps["options"];
}

const EditableCell: React.FC<EditableCellProps> = ({
  editing,
  dataIndex,
  title,
  inputType,
  record,
  index,
  children,
  options,
  ...restProps
}) => {
  const inputNode = (inputType: string) => {
    if (inputType === "number") {
      return <InputNumber />;
    }
    if (inputType === "select" && options) {
      return <Select options={options} />;
    }
    return <Input />;
  };

  return (
    <td {...restProps}>
      {editing ? (
        <Form.Item
          name={dataIndex}
          style={{ margin: 0 }}
          rules={[
            {
              required: true,
              message: `Please input ${title}!`,
            },
          ]}
        >
          {inputNode(inputType)}
        </Form.Item>
      ) : (
        children
      )}
    </td>
  );
};

const Order: React.FC = () => {
  const [form] = Form.useForm();
  const { data: orderData } = useGetOrder();
  const [data, setData] = useState(orderData || []);
  const [editingKey, setEditingKey] = useState("");
  const masterData = useRecoilValue(masterDataState);

  useEffect(() => {
    if (orderData) setData(orderData);
  }, [orderData]);

  const platformOptions: SelectProps["options"] = useMemo(() => {
    const array = masterData?.shop_platform
      ? masterData?.shop_platform.map((item) => ({
          label: item,
          value: item,
        }))
      : [];
    return array;
  }, [masterData]);

  const supplyCompanyOptions: SelectProps["options"] = useMemo(() => {
    const array = masterData?.supply_company
      ? masterData?.supply_company.map((item) => ({
          label: item,
          value: item,
        }))
      : [];
    return array;
  }, [masterData]);

  const paymentOptions: SelectProps["options"] = useMemo(() => {
    const array = masterData?.payment_gateway
      ? masterData?.payment_gateway.map((item) => ({
          label: item,
          value: item,
        }))
      : [];
    return array;
  }, [masterData]);

  const isEditing = (record: OrderItem) => record.id === editingKey;

  // const edit = (record: Partial<OrderItem> & { id: string }) => {
  //   form.setFieldsValue({
  //     base_code: "",
  //     designer_id: "",
  //     designer_links: "",
  //     raw_data: "",
  //     ...record,
  //   });
  //   setEditingKey(record.id);
  // };

  const cancel = () => {
    setEditingKey("");
  };

  const save = async (key: React.Key) => {
    try {
      const row = (await form.validateFields()) as OrderItem;

      const newData = [...data];
      const index = newData.findIndex((item) => key === item.id);

      if (index > -1) {
        const item = newData[index];
        newData.splice(index, 1, {
          ...item,
          ...row,
        });
        setData(newData);
        setEditingKey("");
      } else {
        newData.push(row);
        setData(newData);
        setEditingKey("");
      }
    } catch (errInfo) {
      console.log("Validate Failed:", errInfo);
    }
  };

  const columns = [
    {
      title: "ID",
      dataIndex: "id",
    },
    {
      title: "Order",
      // editable: true,
      render: (_: any, record: OrderItem) => {
        return (
          <Space direction="vertical" style={{ width: "250px" }}>
            <Typography.Text type="danger">{`$${record.order_price}`}</Typography.Text>
            <Typography.Text>{record.order_code}</Typography.Text>
            <Typography.Text>{record.order_mail}</Typography.Text>
            <Typography.Text>{record.order_phone}</Typography.Text>
            <Typography.Text>{record.order_time}</Typography.Text>
            <Typography.Text>
              Shipping channel:{`${record.order_shipping_price}$`} (
              {record.order_shipping_info})
            </Typography.Text>
          </Space>
        );
      },
    },
    {
      title: "Mock up",
      dataIndex: "mockup_url",
      render: (url: string) => (
        <Image
          width="200px"
          height="200px"
          alt="mockup_url"
          src={url}
          fallback="data:image/png;base64,"
        />
      ),
    },
    {
      title: "Design",
      dataIndex: "design_url",
      render: (url: string) => (
        <Image
          width="200px"
          height="200px"
          alt="design_url"
          src={
            "https://dfstudio-d420.kxcdn.com/wordpress/wp-content/uploads/2019/06/digital_camera_photo-1080x675.jpg"
          }
          fallback="data:image/png;base64,"
        />
      ),
    },

    {
      title: "Order Item Info",
      render: (_: any, record: OrderItem) => {
        return (
          <Space direction="vertical" style={{ width: "250px" }}>
            <Space>
              <Typography.Text strong>SKU:</Typography.Text>
              <Typography.Text>{record.sku}</Typography.Text>
            </Space>

            <Space>
              <Typography.Text strong>Product Type:</Typography.Text>
              <Typography.Text>{record.product_type || "N/A"}</Typography.Text>
            </Space>

            <Space>
              <Typography.Text strong>Variant:</Typography.Text>
              <Typography.Text>{record.variant}</Typography.Text>
            </Space>

            <Space>
              <Typography.Text strong>Other variant:</Typography.Text>
              <Typography.Text>{record.other_variant || "N/A"}</Typography.Text>
            </Space>

            <Space>
              <Typography.Text strong>Quantity:</Typography.Text>
              <Typography.Text>{record.quantity}</Typography.Text>
            </Space>
          </Space>
        );
      },
    },

    {
      title: "Designer Name",
      dataIndex: "designer_name",
    },

    {
      title: "Platform",
      dataIndex: "platform",
    },

    {
      title: "Shop Name",
      dataIndex: "shop_name",
    },

    {
      title: "Supply Company",
      dataIndex: "supply_company",
    },

    {
      title: "Payment Gateway",
      dataIndex: "payment_gateway",
    },

    {
      title: "",
      render: (_: any, record: OrderItem) => {
        const editable = isEditing(record);
        return editable ? (
          <span style={{ whiteSpace: "nowrap" }}>
            <Typography.Link
              onClick={() => save(record.id)}
              style={{ marginRight: 8 }}
            >
              Save
            </Typography.Link>
            <Popconfirm title="Sure to cancel?" onConfirm={cancel}>
              <a>Cancel</a>
            </Popconfirm>
          </span>
        ) : (
          <Typography.Link
            disabled={editingKey !== ""}
            style={{ whiteSpace: "nowrap" }}
            // onClick={() => edit(record)}
          >
            Edit
          </Typography.Link>
        );
      },
    },
  ];

  const mergedColumns = columns.map((col) => {
    // if (!col.editable) {
    //   return col;
    // }
    return {
      ...col,
      // onCell: (record: OrderItem) => ({
      //   record,
      //   inputType: getInputType(col.dataIndex),
      //   dataIndex: col.dataIndex,
      //   title: col.title,
      //   editing: isEditing(record),
      //   options: getOptionData(col.dataIndex),
      // }),
    };
  });

  const getInputType = (key: string) => {
    if (
      key === "platform" ||
      key === "supply_company" ||
      key === "payment_gateway"
    ) {
      return "select";
    }
    if (key === "base_cost") {
      return "number";
    }
    return "text";
  };

  const getOptionData = (key: string) => {
    if (key === "platform") return platformOptions;
    if (key === "supply_company") return supplyCompanyOptions;
    if (key === "payment_gateway") return paymentOptions;
    return undefined;
  };

  return (
    <Form form={form} component={false}>
      <Table
        components={{
          body: {
            cell: EditableCell,
          },
        }}
        bordered
        dataSource={data}
        columns={mergedColumns}
        rowClassName="editable-row"
        pagination={false}
      />
    </Form>
  );
};

export default Order;
